import './App.css';
import About from './Components/About';
import Form from './Components/Form';
import Nabbar from './Components/Nabbar';
import React , { useState } from 'react'
import Alert from './Components/Alert';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";


function App() {


  const [alert, setAlert] = useState(null );

    const showAlert = ( message , type ) => {
     setAlert({
       msg: message ,
       type: type 
      });
      setTimeout(() => {
        setAlert(null);
      }, 1500);
   }
   const [mode, setmode] = useState("dark");

   const changeMode = () => {
    if (mode === "dark")
    {  
          showAlert("Dark mode has been enabled " , "Success");

      setmode("light");
      document.body.style.backgroundColor =  " white ";
    }
    else 
    {      
       showAlert("Light mode has been enabled ", "Success");

      setmode("dark");
      document.body.style.backgroundColor =  " darkblue";
    }
  }
  return (
    <>
    <Router>
            <Routes>
               <Nabbar Title="Texttils"  About="About Us"  mode = {mode} changeMode = {changeMode} />
                <Alert  Alert = {alert}  />
            <Route path="/" element={<div><Form  heading="Enter Your content  "  showAlert = {showAlert} mode = {mode} /></div>} />
            <Route path="/about" element={<div><About/> </div>} />
           </Routes>
   {/* <Nabbar Title="Texttils"  About="About Us"  mode = {mode} changeMode = {changeMode} />
   <Alert  Alert = {alert}  /> */}
   {/* <Form  heading="Enter Your content  "  showAlert = {showAlert} mode = {mode} /> */}
    {/* <About/> */}</Router>
    </>
  );
}

export default App;
