import React , {useState} from 'react'

export default function Form(props) {

     const handleUpClick = () =>{
   // console.log(" the handleup click function clicked");
    let newText = text.toUpperCase();
    setttext(newText);
  
  }  
  const handleOnChange = (event) =>{
   // console.log(" the handle on change function clicked");
    setttext(event.target.value);
  }
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setttext(newText);
  }
  const Cleartext = () => {
    setttext(" ");
  }
  const[text,setttext] = useState(" Enter the text here ");
  // settext(" you have clicked on handle up ");
const toogle = () => { 
  const [myStyle, setmyStyle] = useState({
    colour : "black",
    backgroundColor : "white"
 });
 if ( props.mode === "light ") 
 {
  setmyStyle({ colour : "black",
  backgroundColor : "white"});
 }
 else{
  setmyStyle({ colour : "white",
  backgroundColor : "black"});
 }
}
  return (
    <>  
    <div className="form-floating container  mb-3" style={myStyle} onChange = {tooglefunc}>
        <br></br>
        <h4>{props.heading}</h4>
        <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows ="8"></textarea>
        <br></br>      
        <button  className='btn btn-primary mx-2' onClick={handleUpClick}>Convert To UpperCase </button>
        <button  className='btn btn-primary mx-2' onClick={handleLowClick}>Convert To Low erCase </button>
        <button  className='btn btn-primary mx-2' onClick={Cleartext}>Clear Text </button>

    </div>
    <div className="container my3">
      <h2> Your text Summary </h2>
      <p>{text.split(" ").length} words and {text.length} characters </p>
      <p> {0.008 * text.split(" ").length} Minutes required to read </p>
      <h2>Preview </h2>
      <p>{text}</p>
    </div>
    </>
  )
}
